mapper.update:
UPDATE  user
SET     state=#{state}
WHERE   id=#{id}

UPDATE user

    name=#{name},
    username=#{username},
    role=#{role}

WHERE id=#{id}

mapper.delete:
UPDATE user
SET delete_flag='0'
WHERE id=#{id}

mapper.select:
SELECT name,username,password,role
FROM user_info

    username=#{username}
    AND password=#{password}
    AND role=#{role}

SELECT  COUNT(*) FROM user
WHERE   username=#{username}
         AND state='0'
